import copy
import itertools
import json
import time
from sys import stdout
from aamt.logger import Logger
try:
    import numpy as np
except ModuleNotFoundError:
    pass

def current_time():
    """当前时间"""
    return time.strftime('%Y-%m-%d %H:%M:%S', time.localtime(time.time()))

def current_date():
    """当前日期"""
    return time.strftime('%Y-%m-%d', time.localtime(time.time()))

def progress_bar(i):
    """进度条"""
    c = int(i / 10)
    progress = '\r %2d%% [%s%s]'
    a = '■' * c
    b = '□' * (10 - c)
    msg = progress % (i, a, b)
    stdout.write(msg)
    stdout.flush()

def pairwise(option):
    """pairwise算法"""
    cp = []
    s = []
    for x in eval('itertools.product' + str(tuple(option))):
        cp.append(x)
        s.append([i for i in itertools.combinations(x, 2)])
    Logger.info('笛卡尔积:%s' % len(cp))
    del_row = []
    progress_bar(0)
    s2 = copy.deepcopy(s)
    for i in range(len(s)):
        if i % 100 == 0 or i == len(s) - 1:
            progress_bar(int(100 * i / (len(s) - 1)))
        t = 0
        for j in range(len(s[i])):
            flag = False
            for i2 in [x for x in range(len(s2)) if s2[x] != s[i]]:
                if s[i][j] == s2[i2][j]:
                    t = t + 1
                    flag = True
                    break
            if not flag:
                break
        if t == len(s[i]):
            del_row.append(i)
            s2.remove(s[i])
    res = [cp[i] for i in range(len(cp)) if i not in del_row]
    Logger.info('过滤后:%s' % len(res))
    return res

class NpEncoder(json.JSONEncoder):
    """numpy类型转换"""

    def default(self, obj):
        if isinstance(obj, np.integer):
            return int(obj)
        elif isinstance(obj, np.int64):
            return int(obj)
        elif isinstance(obj, np.floating):
            return float(obj)
        elif isinstance(obj, np.float64):
            return float(obj)
        elif isinstance(obj, np.ndarray):
            return obj.tolist()
        else:
            return super(NpEncoder, self).default(obj)

